import React from "react";
import { Field, reduxForm } from "redux-form";


class FormComp extends React.Component {

  renderError({ error, touched }) {
    if (touched && error) {
      return (
        <div className="ui error message">
          <div className="header">{error}</div>
        </div>
      );
    }

  }
  renderInput = ({ input, label, meta }) => {

    const className = `field ${meta.error && meta.touched ? 'error' : ''}`;
    return (
      <div className={className}>
        <label>{label}</label>
        <input {...input} autoComplete="off" />
        {this.renderError(meta)}
      </div>
    );
  }

  render() {
    //const required = value => value ? undefined : 'Required'
    const { handleSubmit, pristine, reset, submitting, invalid } = this.props;
    return (
      <div className="form">
        <div className="ui raised very padded text container segment container-box">
          <h2 className="ui header">Fill in the form</h2>

          <form
            onSubmit={handleSubmit}
            className="ui form info-form error"
          >

            <div>

              <div className="required field">
                <label>Weight in Kg</label>
                <Field
                  component={this.renderInput}
                  name="weight"
                  //component="input"
                  type="number"
                  placeholder="Enter weight.."
                //validate={[required]}
                />
              </div>

              <div className="required field">
                <label>Age</label>
                <Field
                  component={this.renderInput}
                  name="age"
                  //component="input"
                  type="number"
                  placeholder="Enter age.."
                  // validate={[required]}
                  className="required field"

                />
              </div>
              <div className="required field">
                <label>Height in Cm</label>
                <Field
                  component={this.renderInput}
                  name="height"
                  //component="input"
                  placeholder="Enter height.."
                  type="number"
                  //validate={[required]}
                  className="required field"

                />
              </div>
              <div className="required field">
                <label>Activity Level</label>
                <Field
                  component={this.renderInput}
                  name="activity"
                  component="select"
                  placeholder="Select activity.."
                  className="required field">
                  <option />
                  <option value="Sedentary">Sedentary</option>
                  <option value="Light">Lightly Active</option>
                  <option value="Moderate">Moderately Active</option>
                  <option value="Very">Very Active</option>
                  <option value="Extreme">Extremely Active</option>
                </Field>

              </div>

              <div className="required field">
                <label>Select Gender</label>
                <div >
                  <label>
                    <Field
                      //component={this.renderInput}
                      className="ui radio checkbox"
                      name="gender"
                      component="input"
                      type="radio"
                      value="male"
                    />
                    Male</label>

                  <label>
                    <Field
                      component={this.renderInput}
                      className="ui radio checkbox"
                      name="gender"
                      component="input"
                      type="radio"
                      value="female"
                    />
                    Female</label>

                </div>
              </div>
              <div >
                <button className="buttons submit huge left floated ui button"
                  type="submit" disabled={invalid || submitting}>Submit</button>
                <button className="buttons negative huge right floated ui button"
                  type="button"
                  disabled={pristine || submitting}
                  onClick={reset}
                >
                  Clear Values
          </button>
              </div>
            </div>

          </form>
        </div>
      </div>
    );
  }
}



const validate = formValues => {
  const errors = {};

  if (!formValues.weight) {
    errors.weight = 'You must enter your weight!';
  }
  if (!formValues.age) {
    errors.age = 'You must enter your age!';
  }
  if (!formValues.height) {
    errors.height = 'You must enter your height!';
  }
  if (!formValues.gender) {
    errors.gender = 'You must select your gender!';
  }
  if (!formValues.activity) {
    errors.activity = 'You must select your activity!';
  }
  return errors;
}

FormComp = reduxForm({
  // a unique name for the form
  form: "contact",
  validate
})(FormComp);

export default FormComp;
